import React, { useState } from 'react';
import { gql, useQuery, useMutation } from '@apollo/client';
import { GET_AUTHORS, ADD_BOOK } from '../queries/queries';

export default function AddBook() {
  const [title, setTitle] = useState('');
  const [genre, setGenre] = useState('');

  const DisplayAuthors = () => {
    const { loading, error, data } = useQuery(GET_AUTHORS);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

    return data.authors.map(({ name, id }) => (
      <option key={id} value={id}>{name}</option>
    ));
  }

  const AddBook = (evt) => {
    evt.preventDefault();
    const [addBookMutation, { data, loading, error }] = useMutation(ADD_BOOK);
    if (loading) return 'Submitting...';
    if (error) return `Submission error! ${error.message}`;
    addBookMutation({ variables: { title: title, genre: genre } });
  }

  const handleUpdateAuthor = (evt) => {
    evt.preventDefault();
  }

  return (
    <form onSubmit={AddBook}>
      <input
        type='text'
        name='title'
        placeholder='title'
        onChange={e => setTitle(e.target.value)}
      />
      <input
        type='text'
        name='genre'
        placeholder='genre'
        onChange={e => setGenre(e.target.value)}
      />
      <select id='colours' onChange={handleUpdateAuthor}>
        DisplayAuthors()
      </select>
      <button>submit</button>
    </form>
  );
}
